    // ============================================================================
    // 1. CONFIGURAÇÃO E CONSTANTES
    // ============================================================================
    const CONFIG = {
        mockCode: '12345',
        timerDuration: 300, // 5 minutos em segundos
        emailExpiry: 7200000, // 2 horas em ms
        maxAttempts: 3,
        blockDuration: 86400000, // 24 horas em ms
        assets: {
            emailIcon: 'https://framerusercontent.com/images/sz5ueC0VcN5fohNrik0bUG9oJbI.png',
            lottieMain: 'https://lottie.host/27108e59-5743-41a0-980f-c45cc774f50e/qT7a4kmpdD.lottie',
            lottieResend: 'https://lottie.host/a11872ae-9848-45ba-8871-fa870929372e/YWZlUTBnMU.lottie'
        },
        colors: {
            primary: '#AC865C',     // Marrom Frame
            primaryDark: '#8b6d4d',
            bg: '#FFFFFF',
            text: '#1F1F1F',
            subtle: '#F5F5F5',
            border: '#E0E0E0',
            error: '#e74c3c'
        }
    };

    const STORAGE_KEY = 'frame_auth_v2_state';

    // Estado inicial padrão
    let state = {
        email: null,
        emailTimestamp: null,
        attempts: 0,
        resends: 0,
        timerEnd: null,
        isBlocked: false,
        blockUntil: null
    };

    // ============================================================================
    // 2. GESTÃO DE ESTADO (LOCALSTORAGE)
    // ============================================================================
    function loadState() {
        const saved = localStorage.getItem(STORAGE_KEY);
        if (saved) {
            const parsed = JSON.parse(saved);
            state = { ...state, ...parsed };
            
            // Verificar expiração do e-mail
            if (state.email && (Date.now() - state.emailTimestamp > CONFIG.emailExpiry)) {
                state.email = null; // Resetar e-mail se expirou
            }
        }
    }

    function saveState() {
        localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
    }

    function checkBlockStatus() {
        if (state.isBlocked && state.blockUntil) {
            if (Date.now() < state.blockUntil) {
                return true;
            } else {
                // Desbloquear se o tempo passou
                state.isBlocked = false;
                state.blockUntil = null;
                state.attempts = 0;
                state.resends = 0;
                saveState();
            }
        }
        return false;
    }

    function triggerBlock() {
        state.isBlocked = true;
        state.blockUntil = Date.now() + CONFIG.blockDuration;
        saveState();
        renderBlockInterface();
    }

