    // ============================================================================
    // 6. LÓGICA DO 2FA (INPUTS & VALIDAÇÃO)
    // ============================================================================
    function setupOTPLogic() {
        const inputs = document.querySelectorAll('.code-input');
        
        inputs.forEach((input, index) => {
            // Apenas números
            input.addEventListener('input', (e) => {
                e.target.value = e.target.value.replace(/[^0-9]/g, '');
                if (e.target.value) {
                    if (index < inputs.length - 1) inputs[index + 1].focus();
                    checkCompletion();
                }
            });

            // Backspace e Navegação
            input.addEventListener('keydown', (e) => {
                if (e.key === 'Backspace' && !e.target.value && index > 0) {
                    inputs[index - 1].focus();
                }
            });

            // Smart Paste
            input.addEventListener('paste', (e) => {
                e.preventDefault();
                const pasteData = (e.clipboardData || window.clipboardData).getData('text');
                const cleanData = pasteData.replace(/[^0-9]/g, '').slice(0, 5);
                
                if (cleanData) {
                    cleanData.split('').forEach((char, i) => {
                        if (inputs[i]) inputs[i].value = char;
                    });
                    // Focar no próximo vazio ou no último
                    const lastFilled = Math.min(cleanData.length, 4);
                    inputs[lastFilled].focus();
                    checkCompletion();
                }
            });
        });
    }

    function checkCompletion() {
        const inputs = document.querySelectorAll('.code-input');
        const code = Array.from(inputs).map(i => i.value).join('');

        if (code.length === 5) {
            validateCode(code);
        }
    }

    function validateCode(code) {
        const inputs = document.querySelectorAll('.code-input');
        
        if (code === CONFIG.mockCode) {
            // SUCESSO
            inputs.forEach(i => {
                i.style.borderColor = '#27ae60';
                i.style.color = '#27ae60';
                i.disabled = true;
            });
            showToast("Verificação concluída com sucesso!");
            setTimeout(() => {
                // Aqui redirecionaria ou fecharia
                document.getElementById('frame-auth-root').innerHTML = '<div style="color:#AC865C; font-size:24px; font-weight:bold;">Acesso Permitido</div>';
            }, 1000);
            
            // Limpar estado após sucesso
            localStorage.removeItem(STORAGE_KEY);
        } else {
            // ERRO
            handleErrorAttempt(inputs);
        }
    }

    function handleErrorAttempt(inputs) {
        // Animação de erro
        inputs.forEach(i => {
            i.classList.add('shake');
            i.value = '';
        });

        state.attempts += 1;
        saveState();

        setTimeout(() => {
            inputs.forEach(i => i.classList.remove('shake'));
            inputs[0].focus();
        }, 500);

        if (state.attempts >= CONFIG.maxAttempts) {
            triggerBlock();
        }
    }

    function handleResend() {
        const btn = document.getElementById('resend-btn');
        if (btn.classList.contains('disabled')) return;

        state.resends += 1;
        
        if (state.resends >= CONFIG.maxAttempts) {
            triggerBlock();
            return;
        }

        saveState();
        
        // Simular envio
        btn.innerHTML = 'Enviando...';
        btn.classList.add('disabled');
        
        // Modal Lottie temporário
        const modal = document.createElement('div');
        modal.style.cssText = 'position:fixed; top:50%; left:50%; transform:translate(-50%,-50%); background:white; padding:20px; border-radius:16px; box-shadow:0 10px 30px rgba(0,0,0,0.2); z-index:10002; text-align:center;';
        modal.innerHTML = `<dotlottie-player src="${CONFIG.assets.lottieResend}" background="transparent" speed="1" loop autoplay style="width:100px;height:100px;"></dotlottie-player><p>Reenviando...</p>`;
        document.body.appendChild(modal);

        setTimeout(() => {
            modal.remove();
            btn.innerHTML = 'Código reenviado!';
            setTimeout(() => {
                btn.innerHTML = 'Reenviar código';
                btn.classList.remove('disabled');
            }, 2000);
            // Reset timer logic could go here if needed
        }, 2000);
    }

    // ============================================================================
    // 7. TIMER E UTILITÁRIOS
    // ============================================================================
    function startTimer() {
        const display = document.getElementById('timer-display');
        const resendBtn = document.getElementById('resend-btn');
        
        const interval = setInterval(() => {
            const now = Date.now();
            const remaining = Math.max(0, Math.ceil((state.timerEnd - now) / 1000));
            
            const minutes = Math.floor(remaining / 60).toString().padStart(2, '0');
            const seconds = (remaining % 60).toString().padStart(2, '0');
            
            if (display) display.textContent = `${minutes}:${seconds}`;

            if (remaining <= 0) {
                clearInterval(interval);
                disableInterface();
            }
        }, 1000);
    }

    function disableInterface() {
        const inputs = document.querySelectorAll('.code-input');
        inputs.forEach(i => i.disabled = true);
        
        const modal = document.createElement('div');
        modal.className = 'f-modal-backdrop';
        modal.innerHTML = `
            <div class="f-modal">
                <h2>Tempo Expirado</h2>
                <p style="color:#666;">Por segurança, sua sessão expirou.</p>
                <button class="action-btn" onclick="location.reload()">Reiniciar</button>
            </div>
        `;
        document.body.appendChild(modal);
    }

    function showToast(msg) {
        const toast = document.createElement('div');
        toast.className = 'f-toast';
        toast.style.width = 'auto';
        toast.style.maxWidth = '300px';
        toast.style.padding = '12px 20px';
        toast.innerHTML = `<span style="font-weight:500; font-size:14px; color:#1F1F1F;">${msg}</span>`;
        document.body.appendChild(toast);
        setTimeout(() => {
            toast.style.opacity = '0';
            setTimeout(() => toast.remove(), 500);
        }, 4000);
    }

